export default class extends React.Component{
    render () {
        return <div>
            <h1>!Hola Nico</h1>
            <p>Bienvenido al curso</p>
            <img src="/static/dante.png" alt="Dante" />
            <style jsx>{`
                h1 {
                    color: red; 
                }
                div :global(p) {
                    color:green;
                }
                img {
                    max-width: 50%;
                    display: block;
                    margin: 0 auto;
                }
            `}</style>

             <style jsx global>{`
                body {
                    background: yellow;
                }
            `}</style>
        </div>
    }

}
