import 'isomorphic-fetch'
import Link from 'next/link'
import Layout from '../componets/Layout'


export default class extends React.Component{

    static async getInitialProps(){
        let req = await fetch
        ('https://api.audioboom.com/channels/recommended')
        let {body: channels} = await req.json()
        return {channels}
    }

    render () {
        const {channels } = this.props


        return <Layout title="Podcasts"> 

            <div className="card">
            { channels.map((channel) =>
            ( 
                <Link href={`/channel?id=${ channel.id }`} prefetch>
                <a className="imgBx">
                     <img src={ channel.urls.logo_image.original} alt=""/>
                    <h2>{channel.title}</h2>
                </a>
                </Link>  


            )) }
            </div>
                <div></div>

             <style jsx>{`

                .channels {
                    margin: 0;
                    padding: 0;
                    display: flex;
                    justify-content:center;
                    align-items: center;
                    min-height: 100vh;
                    background: #212121;
                    font-family: sans-serif;
                }
                .card{
                    position: relative;
                    width: 300px;
                    height: 350px;
    background: #fff;
    margin: 0 auto;
    border-radius: 4px;
    box-shadow:0 2px 10px rgba(0,0,0,.2);
  }
  .card:before,
  .card:after
  {
    content:"";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 4px;
    background: #fff;
    transition: 0.5s;
    z-index:-1;
  }
  .card:hover:before{
  transform: rotate(20deg);
  box-shadow: 0 2px 20px rgba(0,0,0,.2);
  }
  .card:hover:after{
  transform: rotate(10deg);
  box-shadow: 0 2px 20px rgba(0,0,0,.2);
  }
  .card .imgBx{
  position: absolute;
  top: 10px;
  left: 10px;
  bottom: 10px;
  right: 10px;
  background: #222;
  transition: 0.5s;
  z-index: 1;
  }

  .card:hover .imgBx
  {
    bottom: 80px;
  }

  .card .imgBx img{
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      object-fit: cover;
  }

  .card .details{
      position: absolute;
      left: 10px;
      right: 10px;
      bottom: 10px;
      height: 60px;
      text-align: center;
  }

  .card .details h2{
   margin: 0;
   padding: 0;
   font-weight: 600;
   font-size: 20px;
   color: #777;
   text-transform: uppercase;
  } 

  .card .details h2 span{
  font-weight: 500;
  font-size: 16px;
  color: #f38695;
  display: block;
  margin-top: 5px;
   } 
                .channel {
                    display: block;
                    border-radius: 3px;
                    box-shadow: 0px 2px 6px rgba(0,0,0,0.15);
                    margin-bottom: 0.5em:

                }
                a.channel {
                    display: block;
                    margin-bottom: 0.5em;
                    color: #333;
                    text-decoration: none;

                }
                .channel img {
                    border-radius: 3px;
                    box-shadow: 0px 2px 6px rgba(0,0,0,0.1);
                    width: 100%;
                }
                h1 {
                    font-weight: 600;
                    padding: 15px;
                }
                h2 {
                    padding: 5px;
                    font-size: 0.9em;
                    font-weight: 600;
                    margin: 0;
                    text-align: center;
                }
            `}</style>
            </Layout> 
    }

}
