export default class extends React.Component {

    static async getInitialProps({ query }){
        let idChannel = query.id
        let reqChannel = await fetch(`https://api.audioboom.com/channels/${idChannel}`)
        let dataChannel = await reqChannel.json()
        let channel = dataChannel.body.channel 
        let reqAudios = await fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
        let dataAudios = await reqAudios.json()
        let audioClips = dataAudios.body.audio_clips 
        let reqSeries = await fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`)
        let dataSeries = await reqSeries.json()
        let audioSeries = dataSeries.body.audio_clips  
        return { channel, audioClips, audioSeries }
    }
    render () {
        const { channel, audioClips } = this.props
    return <div>
            <header>Podcasts</header> 
            <h1>{ channel.title }</h1>
            { audioClips.map((clip) => (
                <div>{ clip .title}</div>
            ))}
            <style jsx>{`
                header {
                    color: #fff; 
                    background: #8756ca;
                    padding: 15px;
                    text-align: center; 
                }
                .channels {
                    display: grid;
                    grip-gap: 15px;
                    padding: 15px;
                    grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
                }

                h1 {
                    font-weight: 600;
                    padding: 15px;
                }
                h2 {
                    padding: 5px;
                    font-size: 0.9em;
                    font-weight: 600;
                    margin: 0;
                    text-align: center;
                }
            `}</style>
            </div>
    }
}
